mapper.update:
update t_sys_module
set modulename = #{modulename,jdbcType=VARCHAR},
  parent = #{parent,jdbcType=BIGINT},
  updatetime = #{updatetime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, modulename, parent, updatetime
from t_sys_module
where id = #{id,jdbcType=BIGINT}

select sm.* ,(SELECT count(*) from T_SYS_MODULE where parent=sm.id) as haschild from T_SYS_MODULE sm where
parent=0
parent=#{moduleid,jdbcType=BIGINT}

select id from t_sys_module
where parent=#{moduleid,jdbcType=BIGINT}
and id in

    #{mid,jdbcType=BIGINT}
